const popupOverlay=document.querySelector(".popup-overlay"),skipButton=document.querySelector(".skip-button"),visitButton=document.querySelector(".visit-button"),nsAd=document.querySelector(".ns");let remainingTime=5,allowedToSkip=!1,popupTimer;const showAd=()=>{popupOverlay.classList.add("active"),nsAd.classList.add("active-ad"),popupTimer=setInterval(()=>{console.log(remainingTime),skipButton.innerHTML=`Skip in ${remainingTime}s`,--remainingTime<0&&(allowedToSkip=!0,skipButton.innerHTML="Skip",clearInterval(popupTimer))},1e3)},skipAd=()=>{popupOverlay.classList.remove("popup-overlay"),nsAd.classList.remove("active-ad")};skipButton.addEventListener("click",()=>{allowedToSkip&&skipAd()});const startTimer=()=>{window.scrollY>100&&(showAd(),window.removeEventListener("scroll",startTimer))};window.addEventListener("scroll",startTimer);